import { useEffect, useState } from 'react';
import { getBooks } from '../utils/api';
import BookCard from '../components/BookCard';

const Home = () => {
  const [books, setBooks] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchBooks = async () => {
      try {
        const data = await getBooks();
        console.log('Fetched books data:', data);
        setBooks(data.slice(0, 3)); // Show 3 featured books
      } catch (err) {
        console.error('Fetch books error:', err.message);
        setError('Failed to load books. Check the backend.');
      }
    };
    fetchBooks();
  }, []);

  if (error) return <div style={{ color: 'red', padding: '20px' }}>{error}</div>;

  return (
    <div
      style={{
        minHeight: '100vh',
        minWidth:'10',
        backgroundImage: 'url(https://via.placeholder.com/1920x1080/87CEEB/FFFFFF?text=Library+Background)',
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        backgroundRepeat: 'no-repeat',
        position: 'relative',
      }}
    >
      {/* Overlay to improve text readability */}
      <div
        style={{
          position: 'absolute',
          top: 0,
          left: 0,
          width: '100%',
          height: '100%',
          backgroundColor: 'rgba(0, 0, 0, 0.5)', // Semi-transparent overlay
          zIndex: 1,
        }}
      />
      <div
        style={{
          position: 'relative',
          zIndex: 2,
          padding: '40px',
          color: 'white',
        }}
      >
        <div className="hero">
          <h1 style={{ fontSize: '2.5rem', marginBottom: '1rem' }}>Welcome to the Library</h1>
          <p style={{ fontSize: '1.2rem', marginBottom: '1.5rem' }}>
            Explore a world of knowledge with our vast collection of books. Borrow, review, and manage your reading journey!
          </p>
          <a href="/books" className="btn btn-primary" style={{ display: 'inline-block', padding: '10px 20px' }}>
            Browse All Books
          </a>
        </div>
        <h2 style={{ marginTop: '2rem', fontSize: '2rem' }}>Featured Books</h2>
        <div className="grid" style={{ marginTop: '1rem' }}>
          {books.length > 0 ? (
            books.map(book => <BookCard key={book.id} book={book} />)
          ) : (
            <p>No featured books available.</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default Home;
